import ReactDOM from 'react-dom';
import React, { Component } from 'react'

import './index.css';

class Home extends Component {

    constructor (props){

        super(props);
        this.state = {
          current_page: [],
          next_page: [],
          max_pages: [],
          events: [],
          isLoading: 1,
          events_url: "/wp-json/occasiongenius/v1/events?page=",
          loadingText: "Loading Current Events..."
        }

    }

    componentDidMount() {

        Promise.all([
          fetch('/wp-json/occasiongenius/v1/events'),
        ])
        .then(([res1]) => Promise.all([res1.json()]))
        .then(([data1]) => this.setState({
          events: data1.events,
          current_page: data1.info.current_page, 
          next_page: data1.info.next_page, 
          max_pages: data1.info.max_pages, 
          isLoading: 0
        }));

    } 

    handleEvent(){

        console.log(this.props);  

    }     

    fetchData = async (url) => {

        await fetch(url)
          .then((r) => r.json())
          .then((result) => {
            this.setState({
              events: result.events,
              isLoading: 0
            });
          })
          .catch((e) => {
            console.log(e);
          });

    };  

    nextPage = () => {

        this.setState({
            current_page: this.state.current_page + 1,
            isLoading: 1,
            loadingText: "Loading Next Page of Events..."
          },
          () => {
            const events_url = this.state.events_url + this.state.current_page;
            this.fetchData(events_url);
            window.scrollTo({
              top: 0,
              behavior: "smooth"
            });
          }
        );

    };

    prevPage = () => {

        this.setState({
            current_page: this.state.current_page - 1,
            isLoading: 1,
            loadingText: "Loading Previous Page of Events..."
          },
          () => {
            const events_url = this.state.events_url + this.state.current_page;
            this.fetchData(events_url);
            window.scrollTo({
              top: 0,
              behavior: "smooth"
            });
          }
        );

    };

    render(){
        return (
            <div>
                <h1>home</h1>
            </div>
        )
    }
}

export default Home
