import React from 'react';
import { Link } from "react-router-dom";
class OGHeading extends React.Component {

    constructor(props) {
        super(props);

        this.state = {
            inputValue: this.props.value,
        };
    }

    handleChange = e => {
        console.log(e);
        this.setState({ inputValue: e.target.value });
        this.props.onChange(e);
    };

    render () {
        return ( 

            <>             
                {this.props.showBack ? (
                    <>
                        <div class="occassiongenius-header">
                            <h1 class="occassiongenius-no-margin">Local Events</h1>
                            <div class="occassiongenius-bte">
                                <Link to="/events/" className="return_to_all_events">&lt; Return to All Events</Link>
                            </div>    
                        </div>
                    </>
                ) : (
                    <>
                        <div class="occassiongenius-header">
                            <h1>Local Events</h1>
                        </div>

                        <div class="occassiongenius-search"> 
                            <form action="" method="GET">
                                <div class="occasiongenius-search-form-group">
                                    <label for="og_search">Name</label>
                                    <input type="text" name="og_search" id="og_search" defaultValue="" placeholder="Search by name..." onChange={this.handleChange} />
                                </div>                                                                                                          
                            </form>
                        </div>            
                    </>
                )}        
            </>
        );
    }   
}

export default OGHeading;
