import React, { Component } from 'react';
import { Link } from "react-router-dom";
class EventSingle extends React.Component {

    constructor (props){

        super(props);
        this.state = {
          event: [],
          isLoading: 1,
          events_url: "/wp-json/occasiongenius/v1/event/",
          loadingText: "Loading Event Data"
        }

    }

    componentDidMount() {

        const events_url = this.state.events_url + this.props.uuid;
        console.log(events_url);

        Promise.all([
            fetch(events_url)
        ])
        .then(([res1]) => Promise.all([res1.json()]))
        .then(([data1]) => this.setState({            
            event: data1.event,
            isLoading: 0
        }).catch((err) => {
            console.log(err);
        }));

        console.table(this.state.event);

    }

    render() {

        return ( 

            <div className="occasiongenius-single-page">

                Details for {this.props.uuid}

            </div>

        );

    }

}

export default EventSingle;
