import React from 'react';
import Loading from './Loading';
import { Link } from 'react-router-dom';

class UpcomingForYou extends React.Component {

    constructor (props){

        super(props);
        this.state = {
            events: [],
            isLoading: 1
        }

    }

    componentDidMount() {

        Promise.all([
            fetch('/wp-json/occasiongenius/v1/events?limit=4' ),
          ])
          .then(([res]) => Promise.all([res.json()]))
          .then(([cat_data]) => this.setState({
            events: cat_data.events,
            isLoading: 0
        }));

    }

    render() {

        console.table(this.state.events);

        return (
            <>

                <div className="flex bg-slate-300 mb-16 og-upcoming-for-you pt-4 pb-4">
                    <div className="col-span-12">
                        <div className="flow-root">
                            <p className="float-left text-gray-800 font-semibold mb-0 text-2xl pl-2">
                                Upcoming Events
                            </p>
                        </div>
                    </div>                    

                    {this.state.isLoading ? (

                        <Loading />

                    ) : (
                        <>
                            <div className="grid grid-cols-4 gap-4">

                                {this.state.events.map((item, index) => (   
                                    <>
                                        <div className="" key={index}>
                                            {item.name}
                                        </div>
                                    </>
                                ))}

                            </div>
                        </>
                    )}  

                </div>
            </>
        );

    }

}

export default UpcomingForYou;
