import React from 'react';
import ReactDOM from 'react-dom';
import './index.css';


class App extends React.Component {
  constructor (props){
    super(props);
    this.state = {
      current_page: [],
      next_page: [],
      max_pages: [],
      events: [],
      isLoading: 1,
      events_url: "/wp-json/occasiongenius/v1/events?page="
    }
  }
  componentDidMount() {
    Promise.all([
      fetch('/wp-json/occasiongenius/v1/events'),
    ])
    .then(([res1]) => Promise.all([res1.json()]))
    .then(([data1]) => this.setState({
      events: data1.events,
      current_page: data1.info.current_page, 
      next_page: data1.info.next_page, 
      max_pages: data1.info.max_pages, 
      isLoading: 0
    }));
  } 
  handleEvent(){
    console.log(this.props);  
  }     

  fetchData = async (url) => {
    await fetch(url)
      .then((r) => r.json())
      .then((result) => {
        this.setState({
          events: result.events,
          isLoading: 0
        });
      })
      .catch((e) => {
        console.log(e);
      });
  };  

  nextPage = () => {
    this.setState({
        current_page: this.state.current_page + 1
      },
      () => {
        const events_url = this.state.events_url + this.state.current_page;
        this.fetchData(events_url);
        window.scrollTo(0, 0);
      }
    );
  };

  prevPage = () => {
    this.setState({
        current_page: this.state.current_page - 1
      },
      () => {
        const events_url = this.state.events_url + this.state.current_page;
        this.fetchData(events_url);
        window.scrollTo(0, 0);
      }
    );
  };


  render(){
    const { isLoading, events } = this.state;

    return ( 
      <div className="occasiongenius-container">
        events here 
        Image||Name||Location||Date

        {this.state.events.map((item, index) => (
          <div className="occasiongenius-single-item">
            { item.image_url }<br />
            { item.name }<br />
            { item.venue_city }, { item.venue_state }<br />
            { item.start_date }
          </div>
        ))}

        <div className="posts-app__post-nav">
          <button onClick={this.prevPage}>Previous Page</button>
          <p>Page {this.state.current_page} of {this.state.max_pages}</p>
          <button onClick={this.nextPage}>Next Page</button>
        </div>

      </div>
    );
  }
}

const targets = document.querySelectorAll('.og-root');
Array.prototype.forEach.call(targets, target => {
  const id = target.dataset.id;
  const settings = window.wcSettings[id];

  ReactDOM.render(React.createElement(App, null), target);
});
