import React, { Component } from 'react'
import EventCategory from '../Components/event_category';
import '../index.css';

class Home extends Component {

    constructor (props){

        super(props);
        this.state = {
          current_page: [],
          next_page: [],
          max_pages: [],
          events: [],
          isLoading: 1,
          events_url: "/wp-json/occasiongenius/v1/events?page=",
          loadingText: "Loading Current Events..."
        }

    }

    componentDidMount() {

        Promise.all([
          fetch('/wp-json/occasiongenius/v1/events'),
        ])
        .then(([res1]) => Promise.all([res1.json()]))
        .then(([data1]) => this.setState({
          events: data1.events,
          current_page: data1.info.current_page, 
          next_page: data1.info.next_page, 
          max_pages: data1.info.max_pages, 
          isLoading: 0
        }));

        if(this.props.number){
          this.nextPage();
          this.setState({
            current_page: this.props.number
          });
        }

        document.title = "Local Events";

    } 

    handleEvent(){

        console.log(this.props);  

    }     

    render(){

        return (
            <>
                {this.state.isLoading ? (
                  <>

                    [loading]                    

                  </>
                ) : (
                  <>
                      {JSON.parse(window.ogSettings.og_featured_flags).map((item, index) => (
                          <>
                              <EventCategory event_cat_id={item} key={index} />
                          </>
                      ))}
                  </>
                )}

            </>
        )
    }
}

export default Home
