import React from 'react';
class EventSingle extends React.Component {

    constructor (props){

        super(props);
        this.state = {
          event: [],
          event_dates: [],
          event_count: [],
          isLoading: 1,
          events_url: "/wp-json/occasiongenius/v1/event/",
          loadingText: "Loading Event Data"
        }

    }

    componentDidMount() {

        const event_url = this.state.events_url + this.props.uuid;

        Promise.all([
            fetch(event_url)
        ])      
        .then(([res]) => Promise.all([res.json()]))
        .then(([data]) => this.setState({            
            event: data.event,
            event_dates: data.event.event_dates,
            event_count: data.event.event_dates_count,
            isLoading: 0
        }));

    }

    render() {

        return ( 
            <>
                {(() => {
                    if (this.state.isLoading) {
                        document.title = "Loading...";

                        return (
                            <div className="occassiongenius-loaded">Loading Event Details...</div>
                        )
                    } else {
                        if(this.state.event.venue_name){
                            document.title = this.state.event.name + " - " + this.state.event.venue_name + " (" + this.state.event.venue_city + ", " + this.state.event.venue_state + ")";
                        }else{
                            document.title = this.state.event.name;
                        }

                        return (

                            <>

                                <div className="container mx-auto" data-popularity={ this.state.event.popularity_score }> 
                                    <div className="mx-auto">
                                        <div className="mt-3 md:mt-4 lg:mt-0 flex flex-col lg:flex-row items-strech justify-center lg:space-x-8">
                                            <div className="lg:w-1/2 flex justify-between items-strech bg-gray-50  px-2 py-20 md:py-6 md:px-6 lg:py-24">
                                                <div className="slider">
                                                    <div className="flex">
                                                        <img src={this.state.event.image_url} alt={this.state.event.name} className="w-full h-full" />
                                                    </div>
                                                </div>
                                            </div>

                                            <div className="lg:w-1/2 flex flex-col justify-center mt-7 md:mt-8 lg:mt-0 pb-8 lg:pb-0">
                                                <h1 className="text-3xl lg:text-4xl font-semibold text-gray-800">
                                                    {this.state.event.name}
                                                </h1>
                                                <p className="text-base leading-normal text-gray-600 mt-2">
                                                    { this.state.event.description }
                                                </p>

                                                <p className="text-3xl font-medium text-gray-600 mt-8 md:mt-10"></p>

                                                <div className="flex items-center flex-col md:flex-row space-y-4 md:space-y-0 md:space-x-6 lg:space-x-8 mt-8 md:mt-16">
                                                    <button className="w-full md:w-3/5 border border-gray-800 text-base font-medium leading-none text-white uppercase py-6 focus:outline-none focus:ring-2 focus:ring-offset-2 focus:ring-gray-800 bg-gray-800 text-white">Add to Cart</button>
                                                    <button className="w-full md:w-2/5 border border-gray-800 text-base font-medium leading-none text-gray-800 uppercase py-6 bg-transparent focus:outline-none focus:ring-2 focus:ring-offset-2  focus:ring-gray-800 hover:bg-gray-800 hover:text-white">View Details</button>
                                                </div>
                                            </div>
                                        </div>
                                    </div>
                                </div>

                                <div className="container mx-auto flex justify-center items-center px-4">
                                    <div className="bg-white dark:bg-gray-800 px-3 md:px-4 py-12 flex flex-col justify-center items-center">
                                        <h1 className="mt-8 md:mt-12 text-3xl lg:text-4xl font-semibold leading-10 text-center text-gray-800 text-center md:w-9/12 lg:w-7/12 dark:text-white">Welcome to the coveted family of luxe!</h1>
                                        <p className="mt-10 text-base leading-normal text-center text-gray-600 md:w-9/12 lg:w-7/12 dark:text-white">A confirmation email has been sent to your account, johndoe@gmail.com, with the details of shipping and delivery.</p>
                                        <div className="mt-12 md:mt-14 w-full flex justify-center">
                                            <button className="dark:text-white dark:border-white w-full sm:w-auto border border-gray-800 text-base font-medium text-gray-800 py-5 px-14 focus:outline-none focus:ring-2 focus:ring-offset-2 focus:ring-gray-800 hover:bg-gray-800 hover:text-white dark:hover:text-white dark:hover:bg-gray-700">Back to store</button>
                                        </div>
                                    </div>
                                </div>


                            <div className="occasiongenius-single-page" data-popularity={ this.state.event.popularity_score } itemscope itemtype="https://schema.org/Event">


                                <div className="occassiongenius-single-times"> 
                                    <span itemprop="startDate" content={this.state.event.start_date}>
                                    { this.state.event.start_date } 
                                    {this.state.event.venue_address_2 &&
                                        <>
                                            - { this.state.event.end_date }
                                        </>
                                    }
                                    </span>
                                </div>

                                <div className="occassiongenius-single-venue-information" data-venue_uuid={ this.state.event.venue_uuid } itemprop="location" itemscope itemtype="https://schema.org/Place">
                                    <h4 className="occassiongenius-single-venue-information-title">{ this.state.event.venue_name }</h4>
                                    <address itemprop="address" itemscope itemtype="https://schema.org/PostalAddress">
                                        { this.state.event.venue_address_1 }<br />
                                        {this.state.event.venue_address_2 &&
                                            <>
                                                { this.state.event.venue_address_2 }<br />
                                            </>
                                        }
                                        { this.state.event.venue_city }, { this.state.event.venue_state } { this.state.event.venue_zip } { this.state.event.venue_country }<br />
                                    </address>
                                </div>

                                {this.state.event_count > 1 &&
                                    <>
                                        <div className="occassiongenius-alternative-dates">
                                            <h3>Other Date(s)</h3>
                                            <ul> 
                                            {this.state.event_dates.map((item, index) => (   
                                                <>
                                                    <li>{item}</li>
                                                </>
                                            ))}
                                            </ul>
                                        </div>
                                    </>
                                }

                                {this.state.event.ticket_url &&
                                    <a href={ this.state.event.ticket_url } className="occassiongenius-single-venue-get-tickets" rel="noopener noreferrer" target="_blank" title="Get Tickets">
                                        Get Tickets
                                    </a>
                                }

                                <div className="occassiongenius-single-mapping" data-lat={ this.state.event.latitude } data-long={ this.state.event.longitude }></div>

                            </div>

                            </>
                        )
                    }
                })()}
            </>
        );

    }

}

export default EventSingle;
