import React from 'react';
import ReactDOM from 'react-dom';
import './index.css';


class App extends React.Component {
  constructor (props){
    super(props);
    this.state = {
      api_info: [],
      events: [],
      isLoading: 1
    }
  }
  componentDidMount() {
    Promise.all([
      fetch('/wp-json/occasiongenius/v1/events'),
    ])
    .then(([res1]) => Promise.all([res1.json()]))
    .then(([data1]) => this.setState({
      events: data1.events,
      api_info: data1.info, 
      isLoading: 0
    }));
  } 
  handleEvent(){
    console.log(this.props);  
  }     

  fetchData = () => {
    fetch("/wp-json/occasiongenius/v1/events?page=2")
    .then(([res1]) => Promise.all([res1.json()]))
    .then(([data1]) => this.setState({
      events: data1.events,
      api_info: data1.info, 
      isLoading: 0
    }));

  }

  render(){
    return ( 
      <div className="occasiongenius-container">
        events here 

        {this.state.events.map((item, index) => (
          <div className="occasiongenius-single-item">
            { item.name }
          </div>
        ))}


        <button onClick={this.fetchData}>Load More</button>
      </div>
    );
  }
}

const targets = document.querySelectorAll('.og-root');
Array.prototype.forEach.call(targets, target => {
  const id = target.dataset.id;
  const settings = window.wcSettings[id];

  ReactDOM.render(React.createElement(App, null), target);
});
