import React from 'react';
class EventSingle extends React.Component {

    constructor (props){

        super(props);
        this.state = {
          event: [],
          isLoading: 1,
          events_url: "/wp-json/occasiongenius/v1/event/",
          loadingText: "Loading Event Data"
        }

    }

    componentDidMount() {

        const event_url = this.state.events_url + this.props.uuid;
        console.log(event_url);

        Promise.all([
            fetch('/wp-json/occasiongenius/v1/event/3d6c2f72-7515-5ed2-bce9-74996a7f61ef')
        ])      
        .then(([res]) => Promise.all([res.json()]))
        .then(([data]) => this.setState({            
            event: data.event,
            isLoading: 0
        }));

        console.table(this.state);

    }

    handleEvent(){

        console.log(this.props);  

    }    

    render() {

        return ( 

            <div className="occasiongenius-single-page">

                Details for {this.props.uuid}

            </div>

        );

    }

}

export default EventSingle;
