import React from 'react';
import Loading from './Loading';
import { Link } from 'react-router-dom';

class UpcomingForYou extends React.Component {

    constructor (props){

        super(props);
        this.state = {
            events: [],
            isLoading: 1
        }

    }

    componentDidMount() {

        Promise.all([
            fetch('/wp-json/occasiongenius/v1/events?limit=4' ),
          ])
          .then(([res]) => Promise.all([res.json()]))
          .then(([cat_data]) => this.setState({
            events: cat_data.events,
            isLoading: 0
        }));

    }

    render() {

        console.table(this.state.events);

        return (
            <>

                <div className="flex bg-slate-300 og-upcoming-for-you pt-4 pb-4">
                    <div className="col-span-12">
                        <div className="flow-root">
                            <p className="text-gray-800 font-semibold mb-0 text-2xl pl-2">
                                Upcoming Events
                            </p>
                        </div>
                    </div>  
                </div>                  
                <div className="flex bg-slate-300 mb-8 og-upcoming-for-you pt-4 pb-4 pl-2 pr-2">
                    {this.state.isLoading ? (

                        <Loading />

                    ) : (
                        <>
                            <div class="flex">
                                <div className="grid grid-cols-4 gap-4">

                                    {this.state.events.map((item, index) => (   
                                        <>
                                            <div className="bg-white p-3" key={index}>
                                                <div className="">
                                                    <div className="grid grid-cols-2 gap-2">
                                                        <div className="bg-gray-800">
                                                            <span className="block">{item.start_date_day}</span>
                                                            <span className="block">{item.start_date_month}</span>
                                                        </div>
                                                        <div className="">
                                                            <Link to={`/details/${ item.slug }/`} className="no-underline">
                                                                {item.name}
                                                            </Link>
                                                        </div>
                                                    </div>
                                                    <div className="">
                                                        <svg className="w-2 inline-block" xmlns="http://www.w3.org/2000/svg" viewBox="0 0 384 512"><path d="M168.3 499.2C116.1 435 0 279.4 0 192C0 85.96 85.96 0 192 0C298 0 384 85.96 384 192C384 279.4 267 435 215.7 499.2C203.4 514.5 180.6 514.5 168.3 499.2H168.3zM192 256C227.3 256 256 227.3 256 192C256 156.7 227.3 128 192 128C156.7 128 128 156.7 128 192C128 227.3 156.7 256 192 256z"/></svg>

                                                        {item.venue_city}, {item.venue_state}
                                                    </div>
                                                </div>
                                            </div>
                                        </>
                                    ))}

                                </div>
                            </div>
                        </>
                    )}  

                </div>
            </>
        );

    }

}

export default UpcomingForYou;
