import React from 'react';

class EventFilter extends React.Component {

    constructor (props){

        super(props);
        this.state = {
            categories: [],
            isLoading: 1,
        }

    }

    componentDidMount() {

        Promise.all([
          fetch('/wp-json/occasiongenius/v1/flags'),
        ])
        .then(([res]) => Promise.all([res.json()]))
        .then(([data]) => this.setState({
          categories: data,
          isLoading: 0
        }));

    } 

    render() {

        return ( 
            <>

                <form action="" method="">

                    <div className="w-11/12 mb-4">
                        <label className="text-gray-700 text-sm font-medium">Start Date</label>
                        <input type="date" name="start_date" id="start_date" className="form-control block w-full px-3 py-1.5 text-base font-normal text-gray-700 bg-white bg-clip-padding border border-solid border-gray-300 rounded transition ease-in-out m-0 focus:text-gray-700 focus:bg-white focus:border-blue-600 focus:outline-none" value="" />
                    </div>

                    <div className="w-11/12 mb-4">
                        <label className="text-gray-700 text-sm font-medium">End Date</label>
                        <input type="date" name="end_date" id="end_date" className="form-control block w-full px-3 py-1.5 text-base font-normal text-gray-700 bg-white bg-clip-padding border border-solid border-gray-300 rounded transition ease-in-out m-0 focus:text-gray-700 focus:bg-white focus:border-blue-600 focus:outline-none" value="" />
                    </div>

                    <div class="w-11/12 mb-4">
                        <label className="text-gray-700 text-sm font-medium">Categories</label>

                        <select class="form-multiselect block w-full mt-1" multiple>
                            {this.state.categories.map((item, index) => (
                                <>
                                    <option value={ item.slug }>{ item.output }</option>
                                </>
                            ))}
                        </select>

                    </div>

                    <div class="w-11/12 mb-4">
                        <label className="text-gray-700 text-sm font-medium">Areas</label>

                        <select class="form-multiselect block w-full mt-1" multiple>
                            <option>Option 1</option>
                            <option>Option 2</option>
                            <option>Option 3</option>
                            <option>Option 4</option>
                            <option>Option 5</option>
                        </select>

                    </div>     

                    <div class="w-11/12">
                        <button className="block w-full border border-gray-800 text-base font-medium leading-none text-white uppercase py-6 focus:outline-none focus:ring-2 focus:ring-offset-2 focus:ring-gray-800 bg-gray-800 hover:text-white no-underline text-center">Filter</button>
                    </div>               

                </form>

            </>

        );

    }

}

export default EventFilter;
