import React from 'react';
import { Link, useNavigate } from "react-router-dom";
class OGHeading extends React.Component {


    constructor(props) {
        super(props);

        this.handleSearchKeyUp = this.keyUpHandler.bind(this, 'OGSearchInput');

        const navigate = useNavigate();
    }

    keyUpHandler(refName, e) {
        if(e.target.value.length >= 3){
            console.log(e.target.value);
            this.setState({isLoading: 1})
        }else{
            this.setState({isLoading: 0})
        }
    }

    render () {
        return ( 

            <>             
                {this.props.showBack ? (
                    <>
                        <div class="occassiongenius-header">
                            <h1 class="occassiongenius-no-margin">Local Events</h1>
                            <div class="occassiongenius-bte">
                                <button onClick={() => navigate.goBack()} className="return_to_all_events">&lt; Return to All Events</button>
                            </div>    
                        </div>
                    </>
                ) : (
                    <>
                        <div class="occassiongenius-header">
                            <h1>Local Events</h1>
                        </div> 

                        <div class="occassiongenius-search"> 
                            <form action="" method="GET">
                                <div class="occasiongenius-search-form-group">
                                    <label for="og_search">Name</label>
                                    <input type="text" name="og_search" id="og_search" defaultValue="" placeholder="Search by name..." onKeyUp={this.handleSearchKeyUp} ref="OGSearchInput" />
                                </div>                                                                                                          
                            </form>
                        </div>            
                    </>
                )}        
            </>
        );
    }   
}

export default OGHeading;
