import React, { Component } from 'react';
import { Link } from "react-router-dom";
class EventSingle extends React.Component {

    constructor (props){

        super(props);
        this.state = {
          event: [],
          isLoading: 1,
          events_url: "/wp-json/occasiongenius/v1/event/",
          loadingText: "Loading Event Data"
        }

    }

    fetchData = async (url) => {

        await fetch(url)
          .then((r) => r.json())
          .then((result) => {
            this.setState({
              event: result,
              isLoading: 0
            });
          })
          .catch((e) => {
            console.log(e);
          });

    };

    componentDidMount() {

        const events_url = this.state.events_url + this.props.uuid;
        this.fetchData(events_url);

    }

    render() {

        return ( 

            <div className="occasiongenius-single-page">

                Details for {this.props.uuid}

            </div>

        );

    }

}

export default EventSingle;
