import React from 'react';
class EventSingle extends React.Component {

    constructor (props){

        super(props);
        this.state = {
          event: [],
          isLoading: 1,
          events_url: "/wp-json/occasiongenius/v1/event/",
          loadingText: "Loading Event Data"
        }

    }

    componentDidMount() {

        const event_url = this.state.events_url + this.props.uuid;
        console.log(event_url);

        Promise.all([
            fetch(event_url).then(res => {
                console.table(res);
                if(res.status >= 400) {
                    throw new Error("Server responds with error!");
                }
                return res.json();
            })  
        ])
        .then(([res1]) => Promise.all([res1.json()]))
        .then(([data1]) => this.setState({            
            event: data1.event,
            isLoading: 0
        }));

        console.table(this.state);

    }

    handleEvent(){

        console.log(this.props);  

    }    

    render() {

        return ( 

            <div className="occasiongenius-single-page">

                Details for {this.props.uuid}

            </div>

        );

    }

}

export default EventSingle;
