import React from 'react';
import { Link } from "react-router-dom";

class EventGridItem extends React.Component {

    render() {

        return ( 
            <>

                <div className="col-span-3 bg-rose-700 rounded-xl h-52 md:h-80 no-underline">
                    <Link to={`/events/details/${ this.props.item.slug }/`} className="no-underline">
                        <img src={ this.props.item.image_url } alt={ this.props.item.name } className="rounded-t-xl max-h-44" />
                        <p className="text-xl text-gray-50 pt-4 pl-3 no-underline text-ellipsis ... overflow-hidden line-clamp-2 h-20 pb-1 mb-0"> { this.props.item.name } </p>
                        <p className="text-xs md:text-lg font-light text-gray-50 pt-0 pl-3 pb-0 mb-0 no-underline"> 
                            { this.props.item.date_formatted } <br />
                            { this.props.item.venue_city }, { this.props.item.venue_state }
                        </p>
                        <span className="text-xs md:text-lg font-light decoration-white	underline text-white text-center block mt-1 underline-offset-4">More Info</span>
                    </Link>
                </div>

            </>

        );

    }

}

export default EventGridItem;
