import React, { Component } from 'react'
import { Link } from "react-router-dom";
import Header from '../Components/Header';
import EventCategorySmall from '../Components/EventCategorySmall';
import PersonalizedEvents from '../Components/PersonalizedEvents';
import ReactGA from 'react-ga';
class Home extends Component {

    constructor (props){

        super(props);
        this.state = {
            user_personalized_events: [],
            user_personalized_events_count: 0,
        }

    }

    componentDidMount() {

        document.title = "Local Events";
        if(localStorage.getItem('og_user_flags') !== null){
            var og_user_flags = JSON.parse(localStorage.getItem('og_user_flags'));

            Promise.all([
                fetch('/wp-json/occasiongenius/v1/personalized?flags=' + og_user_flags.join(",")),
              ])
              .then(([res]) => Promise.all([res.json()]))
              .then(([data]) => this.setState({
                user_personalized_events: data.events,
                user_personalized_events_count: data.total
            }));

        }

        // http://localdev.local/wp-json/occasiongenius/v1/bucket?events=613a1b4b-3565-5efc-b55f-40f7ca58380f,f6940045-c982-524a-82d5-5412947b81f5,c0ec49ae-d2ca-568a-977d-4530b8010513,c0ec49ae-d2ca-568a-977d-4530b8010513,cecb1e91-7c0a-53da-b070-275a203aa836,dd04c3cb-e560-5c45-9053-313b801d5686,dd04c3cb-e560-5c45-9053-313b801d5686,fb32a045-43cd-5c9c-91b8-393aff230706%27

        if(window.ogSettings.og_ga_ua){
            ReactGA.pageview(window.location.pathname + window.location.search);
        }          
    }   

    render(){

        return (
            <>

                <Header />

                {this.state.user_personalized_events_count === 4 &&
                    <>
                        <PersonalizedEvents events={ this.state.user_personalized_events } />
                    </>
                }

                {JSON.parse(window.ogSettings.og_featured_flags).map((item, index) => (
                  <>
                    <EventCategorySmall event_cat_id={item} key={index} />
                  </>
                ))}

                <div className="flex items-center flex-col md:flex-row space-y-4 md:space-y-0 md:space-x-6 lg:space-x-8 mt-8 md:mt-16 og-home-view-all-buttons">
                    <Link to="/events/categories" className="block w-full md:w-3/5 border border-gray-800 text-base font-medium leading-none text-white uppercase py-6 focus:outline-none focus:ring-2 focus:ring-offset-2 focus:ring-gray-800 bg-gray-800 hover:bg-gray-600 hover:text-white no-underline text-center og-home-view-all-categories">
                        View All Categories
                    </Link>

                    <Link to="/events/all" className="block w-full md:w-3/5 border border-gray-800 text-base font-medium leading-none text-white uppercase py-6 focus:outline-none focus:ring-2 focus:ring-offset-2 focus:ring-gray-800 bg-gray-800 hover:bg-gray-600 hover:text-white no-underline text-center og-home-view-all-events">
                        View All Events
                    </Link>                    
                </div>

            </>
        )
    }
}

export default Home
