import React from 'react';

function OGUserLogging( category, flags=null ) {

    var og_user_flags = JSON.stringify(localStorage.getItem('og_user_flags'))

    if (og_user_flags !== null) {
        if(category){
            console.log("Slug Logged: " + category);
            // JSON.stringify(og_categories);
            // localStorage.setItem('data','data')

        }
    }else{
        if(category){
            var og_user_flags = [ category ];
            localStorage.setItem('og_user_flags',og_user_flags);
        }
    }

}

export default OGUserLogging;
