import React from 'react';
import { Link } from "react-router-dom";

class EventCategorySmall extends React.Component {

    constructor (props){

        super(props);
        this.state = {
            events: [],
            category: [],
            isLoading: 1
        }

    }

    componentDidMount() {

        Promise.all([
            fetch('/wp-json/occasiongenius/v1/flag/' + this.props.event_cat_id ),
          ])
          .then(([res]) => Promise.all([res.json()]))
          .then(([cat_data]) => this.setState({
            events: cat_data.events,
            category: cat_data.data,
            isLoading: 0
        }));

        console.log("mounted bitch.");
    }

    render() {

        return ( 
            <>
                <div className="flex items-center justify-center bg-white mb-16">                          
                    <div className="grid grid-cols-12 px-18 gap-5">
                        <div className="col-span-12">
                            <p className="text-gray-800 text-3xl font-semibold">
                                { this.state.category.Output }

                                <button class="bg-purple-600 text-white px-4 py-2 rounded-md text-1xl font-medium hover:bg-purple-800 transition duration-300">View All</button>    
                            </p>
                        </div>

                        {this.state.events.map((item, index) => (   
                            <div className="col-span-3 bg-rose-700 rounded-xl h-52 md:h-80 no-underline" key={index}>
                                <Link to={`/events/details/${ item.slug }`}>
                                    <a className="no-underline">
                                        <img src="https://a0.muscache.com/im/pictures/a433b4d0-8183-4523-b4c5-99b81c5729c1.jpg?im_w=320" className="rounded-t-xl max-h-44" />
                                        <p className="text-xl md:text-3xl text-gray-50 pt-5 pl-3 no-underline"> { item.name } </p>
                                        <p className="text-xs md:text-lg font-light text-gray-50 pt-3 pl-3 pb-10 no-underline"> 117 kilometers away </p>
                                    </a>
                                </Link>
                            </div>
                        ))}

                    </div> 
                </div>

            </>

        );

    }

}

export default EventCategorySmall;
