import React from 'react';
import Loading from './Loading';
import EventGridItem from "./EventGridItem"

class PersonalizedEvents extends React.Component {

    constructor (props){

        super(props);
        this.state = {
            isLoading: 1
        }

    }

    componentDidMount() {

        this.setState({
            isLoading: 0
        });

    }

    render() {

        return ( 
            <>

                <div className="flex items-center justify-center bg-white mb-16">                          
                    <div className="grid grid-cols-12 px-18 gap-5">

                        {this.state.isLoading ? (

                            <Loading />

                        ) : (
                            <>

                                <div className="col-span-12">
                                    <div className="flow-root">
                                        <p className="float-left text-gray-800 text-3xl font-semibold mb-0">
                                            {this.props.title ? (
                                                <>
                                                    { this.props.title }
                                                </>
                                            ) : (
                                                <>
                                                    Recommended Events For You
                                                </>
                                            )}

                                            {this.props.btn_text &&
                                                <>
                                                    <Link to={ this.props.btn_url } className="no-underline">
                                                        <button className="float-right bg-gray-800 text-white px-2 py-2 rounded-none text-base font-medium hover:bg-gray-800 transition duration-300 mt-[5px] pl-[15px] pr-[15px] uppercase text-base pt-[12px] leading-none hover:bg-gray-600">
                                                            { this.props.btn_text }
                                                        </button>    
                                                    </Link>
                                                </>
                                            }
                                        </p>


                                    </div>
                                </div>

                                {this.props.events.map((item, index) => (   
                                    <EventGridItem item={item} key={index} />
                                ))}

                            </>
                        )}     

                    </div> 
                </div>

            </>

        );

    }

}

export default PersonalizedEvents;
