import React, { Component } from 'react';
import { Link } from "react-router-dom";
import Breadcrumbs from './Breadcrumbs';
import Loading from './Loading';
import '../index.css';

class CategoryOutput extends Component {

    constructor (props){

        super(props);
        this.state = {
            category_data: [],
            category_events: [],
            isLoading: 1,
        }

    }

    componentDidMount() {

        Promise.all([
          fetch('/wp-json/occasiongenius/v1/flag/' + this.props.slug),
        ])
        .then(([res]) => Promise.all([res.json()]))
        .then(([cat_data]) => this.setState({
          category_data: cat_data.data,
          category_events: cat_data.events,
          isLoading: 0
        }));

        document.title = "Local Event Categories";

    } 

    render(){

        console.table(this.state.category_data);
        console.table(this.state.category_events);

        return (
            <>

                <Breadcrumbs parent_title="All Categories" parent_url="/events/categories/" page_name="All Categories" />

                [single_category_template]

            </>
        )
    }
}

export default CategoryOutput
