import React from 'react';
import { Link } from 'react-router-dom';
import RelatedEvents from './RelatedEvents';
import Breadcrumbs from './Breadcrumbs';
import Loading from './Loading';
import OGUserLogging from './Tools/OGUserLogging';
class EventSingle extends React.Component {

    constructor (props){

        super(props);
        this.state = {
          event: [],
          isLoading: 1,
          events_url: "/wp-json/occasiongenius/v1/event/",
        }

    }

    componentDidMount() {

        const event_url = this.state.events_url + this.props.uuid;

        Promise.all([
            fetch(event_url)
        ])      
        .then(([res]) => Promise.all([res.json()]))
        .then(([data]) => this.setState({            
            event: data.event,
            isLoading: 0
        }));

        OGUserLogging("", this.props.uuid );

    }

    componentDidUpdate(previousProps, previousState) {

        if (previousProps.uuid !== this.props.uuid) {

            const event_url = this.state.events_url + this.props.uuid;

            Promise.all([
                fetch(event_url)
            ])      
            .then(([res]) => Promise.all([res.json()]))
            .then(([data]) => this.setState({            
                event: data.event,
                isLoading: 0
            }));

            OGUserLogging("", this.props.uuid );

            window.scrollTo(0, 0)

        }

    }

    render() {

        return ( 
            <>
                {(() => {
                    if (this.state.isLoading) {
                        document.title = "Loading...";

                        return (
                            <Loading />
                        )
                    } else {
                        if(this.state.event.venue_name){
                            document.title = this.state.event.name + " - " + this.state.event.venue_name + " (" + this.state.event.venue_city + ", " + this.state.event.venue_state + ")";
                        }else{
                            document.title = this.state.event.name;
                        }

                        return (

                            <>
                                <Breadcrumbs page_flags={ this.state.event.flags} page_name={ this.state.event.name } />

                                <div className="container mx-auto" itemscope="" itemtype="https://schema.org/Event" data-popularity={ this.state.event.popularity_score }> 
                                    <div className="mx-auto">
                                        <div className="mt-3 md:mt-4 lg:mt-0 flex flex-col lg:flex-row items-strech justify-center lg:space-x-8">
                                            <div className="lg:w-1/2 flex justify-between items-strech bg-gray-50 bg-cover bg-center rounded-xl" style={{ backgroundImage: `url(${this.state.event.image_url})` }}>
                                                <img src={this.state.event.image_url} alt={this.state.event.name} className="w-full h-full invisible" itemprop="thumbnail" content={ this.state.event.image_url } />
                                            </div>

                                            <div className="lg:w-1/2 flex flex-col justify-center mt-7 md:mt-8 lg:mt-0 pb-8 lg:pb-0">
                                                <h1 className="text-3xl lg:text-4xl font-semibold text-gray-800" itemprop="name">
                                                    {this.state.event.name}
                                                </h1>
                                                <p className="text-base leading-normal text-gray-600 mt-2" itemprop="description">
                                                    { this.state.event.description }
                                                </p>
                                                <p className="text-base leading-normal text-gray-600 mt-2 font-semibold" itemprop="startDate" datetime={ this.state.event.gcal_start_date }>
                                                    { this.state.event.date_formatted } 
                                                </p>

                                                <div className="w-full mx-auto">
                                                    <h4 className="text-lg font-bold m-0 p-0 mb-1">Share This Event:</h4>
                                                    <div className="flex">
	                                                    <ul className="flex flex-wrap m-0 p-0">
                                                            <li className="p-0 m-0">
                                                                <a href={`https://www.facebook.com/sharer/sharer.php?u=${ window.location.href }`} target="_blank" rel="noreferrer" className="inline-flex items-center mr-1 px-3 py-2 rounded-full bg-gray-100 hover:bg-gray-200 text-sm text-gray-600 no-underline" title="Share this event to Facebook">
                                                                <span>
                                                                    <svg className="w-auto h-4 mr-2 fill-current text-gray-400" xmlns="http://www.w3.org/2000/svg" viewBox="0 0 320 512">
                                                                    <path d="M279.14 288l14.22-92.66h-88.91v-60.13c0-25.35 12.42-50.06 52.24-50.06h40.42V6.26S260.43 0 225.36 0c-73.22 0-121.08 44.38-121.08 124.72v70.62H22.89V288h81.39v224h100.17V288z"></path>
                                                                    </svg>
                                                                </span>
                                                                Facebook
                                                                </a>
                                                            </li>                                                            
                                                            <li className="p-0 m-0">
                                                                <a href={`https://twitter.com/intent/tweet?text=${this.state.event.name} - ${ window.location.href }`} target="_blank" rel="noreferrer" className="inline-flex items-center mr-1 px-3 py-2 rounded-full bg-gray-100 hover:bg-gray-200 text-sm text-gray-600 no-underline" title="Share this event to Twitter">
                                                                <span>
                                                                    <svg className="w-auto h-4 mr-2 fill-current text-gray-400" xmlns="http://www.w3.org/2000/svg" viewBox="0 0 512 512">
                                                                    <path
                                                                            d="M459.37 151.716c.325 4.548.325 9.097.325 13.645 0 138.72-105.583 298.558-298.558 298.558-59.452 0-114.68-17.219-161.137-47.106 8.447.974 16.568 1.299 25.34 1.299 49.055 0 94.213-16.568 130.274-44.832-46.132-.975-84.792-31.188-98.112-72.772 6.498.974 12.995 1.624 19.818 1.624 9.421 0 18.843-1.3 27.614-3.573-48.081-9.747-84.143-51.98-84.143-102.985v-1.299c13.969 7.797 30.214 12.67 47.431 13.319-28.264-18.843-46.781-51.005-46.781-87.391 0-19.492 5.197-37.36 14.294-52.954 51.655 63.675 129.3 105.258 216.365 109.807-1.624-7.797-2.599-15.918-2.599-24.04 0-57.828 46.782-104.934 104.934-104.934 30.213 0 57.502 12.67 76.67 33.137 23.715-4.548 46.456-13.32 66.599-25.34-7.798 24.366-24.366 44.833-46.132 57.827 21.117-2.273 41.584-8.122 60.426-16.243-14.292 20.791-32.161 39.308-52.628 54.253z"
                                                                            ></path>
                                                                    </svg>
                                                                </span>
                                                                Twitter
                                                                </a>
                                                            </li>       

                                                            <li className="p-0 m-0">
                                                                <a href={`https://www.linkedin.com/sharing/share-offsite/?url=${ window.location.href }`} target="_blank" rel="noreferrer" className="inline-flex items-center mr-1 px-3 py-2 rounded-full bg-gray-100 hover:bg-gray-200 text-sm text-gray-600 no-underline" title="Share this event to LinkedIn">
                                                                <span>
                                                                    <svg className="w-auto h-4 mr-2 fill-current text-gray-400" xmlns="http://www.w3.org/2000/svg" viewBox="0 0 448 512">
                                                                    <path
                                                                            d="M100.28 448H7.4V148.9h92.88zM53.79 108.1C24.09 108.1 0 83.5 0 53.8a53.79 53.79 0 0 1 107.58 0c0 29.7-24.1 54.3-53.79 54.3zM447.9 448h-92.68V302.4c0-34.7-.7-79.2-48.29-79.2-48.29 0-55.69 37.7-55.69 76.7V448h-92.78V148.9h89.08v40.8h1.3c12.4-23.5 42.69-48.3 87.88-48.3 94 0 111.28 61.9 111.28 142.3V448z"
                                                                            ></path>
                                                                    </svg>
                                                                </span>
                                                                Linkedin
                                                                </a>
                                                            </li>

                                                            <li className="p-0 m-0">
                                                                <a href={`https://calendar.google.com/calendar/r/eventedit?text=${this.state.event.name}&dates=${ this.state.event.gcal_start_date }/${ this.state.event.gcal_end_date }&details=For+details,+link+here:+${ window.location.href }&location=${ this.state.event.venue_name }+${ this.state.event.venue_address_1 }+${ this.state.event.venue_city }, ${ this.state.event.venue_state } ${ this.state.event.venue_zip } ${ this.state.event.venue_country }`}   target="_blank" rel="noreferrer" className="inline-flex items-center mr-1 px-3 py-2 rounded-full bg-gray-100 hover:bg-gray-200 text-sm text-gray-600 no-underline" title="Add to Calendar">
                                                                    <span>                                                                    
                                                                        <svg className="w-auto h-4 mr-2 fill-current text-gray-400" xmlns="http://www.w3.org/2000/svg" viewBox="0 0 448 512"><path d="M96 32C96 14.33 110.3 0 128 0C145.7 0 160 14.33 160 32V64H288V32C288 14.33 302.3 0 320 0C337.7 0 352 14.33 352 32V64H400C426.5 64 448 85.49 448 112V160H0V112C0 85.49 21.49 64 48 64H96V32zM448 464C448 490.5 426.5 512 400 512H48C21.49 512 0 490.5 0 464V192H448V464z"/></svg>
                                                                    </span>
                                                                    Calendar
                                                                </a>
                                                            </li>

                                                    	</ul>
                                                    </div>
                                                </div>

                                                <p className="text-3xl font-medium text-gray-600 mt-8 md:mt-10"></p>

                                                <div className="flex items-center flex-col md:flex-row space-y-4 md:space-y-0 md:space-x-6 lg:space-x-8 mt-8 md:mt-16">
                                                    {this.state.event.ticket_url &&
                                                        <a itemprop="url" href={this.state.event.ticket_url} target="_blank" rel="noreferrer" className="block w-full md:w-3/5 border border-gray-800 text-base font-medium leading-none text-white uppercase py-6 focus:outline-none focus:ring-2 focus:ring-offset-2 focus:ring-gray-800 bg-gray-800 hover:text-white no-underline text-center">
                                                            Get Tickets
                                                        </a>
                                                    }

                                                    {this.state.event.source_url &&
                                                        <a href={this.state.event.source_url} target="_blank" rel="noreferrer" className="block w-full md:w-2/5 border border-gray-800 text-base font-medium leading-none text-gray-800 uppercase py-6 bg-transparent focus:outline-none focus:ring-2 focus:ring-offset-2  focus:ring-gray-800 hover:bg-gray-800 hover:text-white no-underline text-center">
                                                            More Information
                                                        </a>
                                                    }
                                                </div>
                                            </div>
                                        </div>
                                    </div>
                                </div>

                                <div className="container mx-auto flex justify-center items-center pt-4 pb-4 border border-solid border-zinc-300 mt-12 bg-slate-100">
                                    <div className="flex flex-col w-3/4 justify-center items-center" itemprop="location" itemscope itemtype="https://schema.org/Place">

                                        <h3 className="mt-1 text-2xl font-semibold text-center text-gray-800 text-center md:w-9/12 lg:w-7/12 mb-1 pb-1">
                                            Venue Information
                                        </h3>
                                        <h4 className="mt-1 text-xl font-semibold text-center text-gray-800 text-center md:w-9/12 lg:w-7/12 mb-1 pb-1" itemprop="name">
                                            { this.state.event.venue_name }
                                        </h4>
                                        <p className="text-base leading-normal text-center text-gray-600 md:w-9/12 lg:w-7/12 mb-4" itemprop="address" itemscope itemtype="https://schema.org/PostalAddress">
                                            <span itemprop="streetAddress">{ this.state.event.venue_address_1 }</span><br />
                                            {this.state.event.venue_address_2 &&
                                                <>
                                                    { this.state.event.venue_address_2 }<br />
                                                </>
                                            }
                                            <span itemprop="addressLocality">{ this.state.event.venue_city }</span>, 
                                            <span itemprop="addressRegion">{ this.state.event.venue_state }</span> 
                                            <span itemprop="postalCode">{ this.state.event.venue_zip }</span> 
                                            { this.state.event.venue_country }

                                            <Link to={`/events/venue/${ this.state.event.venue_uuid }`} className="block w-full md:w-2/5 border border-gray-800 text-base font-medium leading-none text-gray-800 uppercase py-6 bg-transparent focus:outline-none focus:ring-2 focus:ring-offset-2  focus:ring-gray-800 hover:bg-gray-800 hover:text-white no-underline text-center mt-4">
                                                See all of venue's events
                                            </Link>
                                        </p>                                        
                                    </div>
                                </div>

                                <RelatedEvents parent_id={ this.state.event.id} flags={ this.state.event.flags } />

                            </>
                        )
                    }
                })()}
            </>
        );

    }

}

export default EventSingle;
